import { Component, OnInit } from '@angular/core';
import { Equipament } from './model/equipament.model';
import { EquipamentService } from './shared/equipament.service';
const EQUIPAMENT: Equipament[] = [
  {
    name: 'Abadominal', img: 'data:image/jpeg;base64,',
    decription: 'Fortalecimento da musculatura abdominal.', qty: 0, id:1
  }
  , {
    name: 'Barra de Flexão', decription: 'Auxílio a flexões de braços vertical e alongamento dos membros superiores.',
    qty: 0, img: 'data:image/png;base64,',
    id:2
  }, {
    name: 'Bicicleta', decription: 'Melhora a resistência aeróbica e a coordenação dos membros inferiores.',id:3,
    qty:0, img:'data:image/jpeg;base64,'
  },
  {
    name: 'Exercitador de Pernas', decription: 'Fortalece a musculatura dos membros inferiores e coxas.', qty: 0,id:4,
    img: 'data:image/jpeg;base64,'
  }

]
@Component({
  selector: 'app-equipament',
  templateUrl: './equipament.component.html',
  styleUrls: ['./equipament.component.css']
})
export class EquipamentComponent implements OnInit {

  display:Equipament[]= EQUIPAMENT
  constructor(private equipamentService:EquipamentService) { }

  ngOnInit(): void {
  }
  add(e) {

    for (let key in this.display) {
      if (this.display[key].id == e.id) {
        this.display[key].qty += 1
        this.equipamentService.add(this.display[key])
      }

    }
  }
  remove(e) {
    for (let key in this.display) {
      if (this.display[key].id == e.id) {
        if(this.display[key].qty >0)
          this.display[key].qty -= 1
        this.equipamentService.remove(this.display[key])
      }

    }

  }
}

